import React, { useState } from 'react';

function VideoForm({ onAddVideo, existingVideos }) {
  const [videoData, setVideoData] = useState({
    title: '',
    type: '',
    releaseYear: '',
    director: '',
    genre: '',
    duration: '',
    actors: '',
    watched: false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setVideoData({ ...videoData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const exists = existingVideos.some(
      (video) =>
        video.title === videoData.title &&
        video.releaseYear === videoData.releaseYear &&
        video.director === videoData.director
    );
    if (!exists) {
      onAddVideo({ ...videoData, actors: videoData.actors.split(',') });
      setVideoData({
        title: '',
        type: '',
        releaseYear: '',
        director: '',
        genre: '',
        duration: '',
        actors: '',
        watched: false,
      });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="title"
        placeholder="Titre"
        value={videoData.title}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="type"
        placeholder="Type (film, dessin animé, etc.)"
        value={videoData.type}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="releaseYear"
        placeholder="Année de sortie"
        value={videoData.releaseYear}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="director"
        placeholder="Réalisateur"
        value={videoData.director}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="genre"
        placeholder="Genre"
        value={videoData.genre}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="duration"
        placeholder="Durée (en minutes)"
        value={videoData.duration}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="actors"
        placeholder="Liste d'acteurs principaux (séparés par des virgules)"
        value={videoData.actors}
        onChange={handleChange}
        required
      />
      <label>
        Vu:
        <input
          type="checkbox"
          name="watched"
          checked={videoData.watched}
          onChange={handleChange}
        />
      </label>
      <button type="submit">Ajouter</button>
    </form>
  );
}

export default VideoForm;
