import React, { useState } from 'react';
import VideoForm from './VideoForm';
import VideoList from './VideoList';
import VideoCounter from './VideoCounter';

function VideoLibrary() {
  const [videos, setVideos] = useState([]);

  const addVideo = (newVideo) => {
    setVideos([...videos, { ...newVideo, id: Date.now() }]);
  };

  const toggleWatched = (id) => {
    setVideos((prevVideos) =>
      prevVideos.map((video) =>
        video.id === id ? { ...video, watched: !video.watched } : video
      )
    );
  };

  const deleteVideo = (id) => {
    setVideos((prevVideos) => prevVideos.filter((video) => video.id !== id));
  };

  return (
    <div>
      <h1>Vidéothèque</h1>
      <VideoForm onAddVideo={addVideo} existingVideos={videos} />
      <VideoList videos={videos} onToggleWatched={toggleWatched} onDelete={deleteVideo} />
    </div>
  );
}

export default VideoLibrary;
