import React, { useState } from "react";

function VideoList({ videos, updateVideo, deleteVideo }) {
  const [editIndex, setEditIndex] = useState(-1);

  const handleEdit = (index) => {
    setEditIndex(index);
  };

  const handleSave = (index) => {
    setEditIndex(-1);
  };

  const markAsWatched = (index) => {
    // Mettez à jour la propriété "watched" de la vidéo à l'index donné
    const updatedVideo = { ...videos[index], watched: true };
    updateVideo(index, updatedVideo);
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Titre</th>
          <th>Type</th>
          <th>Année</th>
          <th>Réalisateur</th>
          <th>Genre</th>
          <th>Durée</th>
          <th>Acteurs</th>
          <th>Vu</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {videos.map((video, index) => (
          <tr key={index}>
            <td>
              {editIndex === index ? (
                <input
                  type="text"
                  value={video.title}
                  onChange={(e) =>
                    updateVideo(index, { ...video, title: e.target.value })
                  }
                />
              ) : (
                video.title
              )}
            </td>
            <td>
              {editIndex === index ? (
                <input
                  type="text"
                  value={video.type}
                  onChange={(e) =>
                    updateVideo(index, { ...video, type: e.target.value })
                  }
                />
              ) : (
                video.type
              )}
            </td>
            <td>
              {editIndex === index ? (
                <input
                  type="number"
                  value={video.year}
                  onChange={(e) =>
                    updateVideo(index, { ...video, year: e.target.value })
                  }
                />
              ) : (
                video.year
              )}
            </td>
            <td>
              {editIndex === index ? (
                <input
                  type="text"
                  value={video.director}
                  onChange={(e) =>
                    updateVideo(index, { ...video, director: e.target.value })
                  }
                />
              ) : (
                video.director
              )}
            </td>
            <td>
              {editIndex === index ? (
                <input
                  type="text"
                  value={video.genre}
                  onChange={(e) =>
                    updateVideo(index, { ...video, genre: e.target.value })
                  }
                />
              ) : (
                video.genre
              )}
            </td>
            <td>
              {editIndex === index ? (
                <input
                  type="text"
                  value={video.duration}
                  onChange={(e) =>
                    updateVideo(index, { ...video, duration: e.target.value })
                  }
                />
              ) : (
                video.duration
              )}
            </td>
            <td>
              {editIndex === index ? (
                <input
                  type="text"
                  value={video.actors}
                  onChange={(e) =>
                    updateVideo(index, { ...video, actors: e.target.value })
                  }
                />
              ) : (
                video.actors
              )}
            </td>
            <td>
              {editIndex === index ? (
                <input
                  type="checkbox"
                  checked={video.watched}
                  onChange={(e) =>
                    updateVideo(index, { ...video, watched: e.target.checked })
                  }
                />
              ) : video.watched ? (
                "Oui"
              ) : (
                "Non"
              )}
            </td>
            <td>
              <button onClick={() => toggleWatchedStatus(index)}>
                {video.watched ? "Non Vu" : "Vu"}
              </button>
              {editIndex === index ? (
                <button onClick={() => handleSave(index)}>Enregistrer</button>
              ) : (
                <button onClick={() => handleEdit(index)}>Modifier</button>
              )}
              <button onClick={() => deleteVideo(index)}>Supprimer</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default VideoList;
