import React from 'react';

function Video({ video, onToggleWatched, onDelete }) {
  return (
    <div>
      <h3>{video.title}</h3>
      <p>Type: {video.type}</p>
      <p>Année de sortie: {video.releaseYear}</p>
      <p>Réalisateur: {video.director}</p>
      <p>Genre: {video.genre}</p>
      <p>Durée: {video.duration} minutes</p>
      <p>Liste d'acteurs principaux: {video.actors.join(', ')}</p>
      <label>
        Vu:
        <input
          type="checkbox"
          checked={video.watched}
          onChange={() => onToggleWatched(video.id)}
        />
      </label>
      <button onClick={() => onDelete(video.id)}>Supprimer</button>
    </div>
  );
}

export default Video;
