import React from 'react';

function VideoList({ videos, onEdit, onDelete }) {
  return (
    <div className="video-list">
      <h3>Liste des vidéos</h3>
      {videos.map((video) => (
        <div key={video.title} className="video-list-item">
          <h4>{video.title}</h4>
          <div className="video">
      <h3>{video.title}</h3>
      <p>Type: {video.type}</p>
      <p>Année de sortie: {video.releaseYear}</p>
      <p>Réalisateur: {video.director}</p>
      <p>Genre: {video.genre}</p>
      <p>Durée: {video.duration} minutes</p>
      <p>Acteurs principaux: {video.actors.join(', ')}</p>
      <label>
        Vu:
        <input type="checkbox" checked={video.watched} readOnly />
      </label>
    </div>
          <button onClick={() => onEdit(video)}>Modifier</button>
          <button onClick={() => onDelete(video.title)}>Supprimer</button>
        </div>
      ))}
    </div>
  );
}

export default VideoList;
