import React, { useState } from 'react';
import Video from './Video';
import VideoForm from './VideoForm';
import VideoList from './VideoList';

function VideoLibrary() {
  const [videos, setVideos] = useState([
    {
      title: 'Exemple de vidéo',
      type: 'Film',
      releaseYear: '2022',
      director: 'Réalisateur Inconnu',
      genre: 'Action',
      duration: '120',
      actors: 'Acteur 1, Acteur 2',
      watched: false,
    },
  ]);

  const [selectedVideo, setSelectedVideo] = useState(null);

  const addVideo = (video) => {
    setVideos([...videos, video]);
  };

  const editVideo = (video) => {
    setSelectedVideo(video);
  };

  const updateVideo = (updatedVideo) => {
    setVideos((prevVideos) =>
      prevVideos.map((video) =>
        video.title === updatedVideo.title ? updatedVideo : video
      )
    );
    setSelectedVideo(null);
  };

  const deleteVideo = (title) => {
    setVideos(videos.filter((video) => video.title !== title));
  };

  return (
    <div className="video-library">
      <h1>Vidéothèque</h1>
      <VideoForm
        onAddVideo={addVideo}
        existingVideos={videos}
        selectedVideo={selectedVideo}
        onUpdateVideo={updateVideo}
      />
      <VideoList videos={videos} onEdit={editVideo} onDelete={deleteVideo} />
      <Video  />
    </div>
  );
}

export default VideoLibrary;
