import React, { useState } from 'react';
import VideoForm from './VideoForm';
import VideoList from './VideoList';

function VideoLibrary() {
  const [videos, setVideos] = useState([]);

  const addVideo = (newVideo) => {
    // Vérifiez si la vidéo existe déjà
    const exists = videos.some(video => video.title === newVideo.title);
    if (!exists) {
      setVideos([...videos, newVideo]);
    }
  }

  const updateVideo = (index, updatedVideo) => {
    // Mettez à jour la vidéo à l'index donné
    const updatedVideos = [...videos];
    updatedVideos[index] = updatedVideo;
    setVideos(updatedVideos);
  }

  const deleteVideo = (index) => {
    // Supprimez la vidéo à l'index donné
    const updatedVideos = videos.filter((video, i) => i !== index);
    setVideos(updatedVideos);
  }

  return (
    <div>
      <h1>Ma Vidéothèque</h1>
      <VideoList videos={videos} updateVideo={updateVideo} deleteVideo={deleteVideo} />
      <VideoForm addVideo={addVideo} videos={videos} />
    </div>
  );
}

export default VideoLibrary;
