import React, { useState } from 'react';

function VideoForm({ addVideo }) {
  const [newVideo, setNewVideo] = useState({
    title: '',
    type: '',
    year: '',
    director: '',
    genre: '',
    duration: '',
    actors: '',
    watched: false,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewVideo({
      ...newVideo,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addVideo(newVideo);
    // Réinitialisez le formulaire
    setNewVideo({
      title: '',
      type: '',
      year: '',
      director: '',
      genre: '',
      duration: '',
      actors: '',
      watched: false,
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="title">Titre :</label>
        <input
          type="text"
          id="title"
          name="title"
          value={newVideo.title}
          onChange={handleInputChange}
          required
        />
      </div>
      <div>
        <label htmlFor="type">Type :</label>
        <input
          type="text"
          id="type"
          name="type"
          value={newVideo.type}
          onChange={handleInputChange}
          required
        />
      </div>
      <div>
        <label htmlFor="year">Année de sortie :</label>
        <input
          type="number"
          id="year"
          name="year"
          value={newVideo.year}
          onChange={handleInputChange}
          required
        />
      </div>
      <div>
        <label htmlFor="director">Réalisateur :</label>
        <input
          type="text"
          id="director"
          name="director"
          value={newVideo.director}
          onChange={handleInputChange}
          required
        />
      </div>
      <div>
        <label htmlFor="genre">Genre :</label>
        <input
          type="text"
          id="genre"
          name="genre"
          value={newVideo.genre}
          onChange={handleInputChange}
          required
        />
      </div>
      <div>
        <label htmlFor="duration">Durée :</label>
        <input
          type="number"
          id="duration"
          name="duration"
          value={newVideo.duration}
          onChange={handleInputChange}
          required
        />
      </div>
      <div>
        <label htmlFor="actors">Acteurs :</label>
        <input
          type="text"
          id="actors"
          name="actors"
          value={newVideo.actors}
          onChange={handleInputChange}
          required
        />
      </div>
      <div>
  <label htmlFor="watched">Vu :</label>
  <input
    type="checkbox"
    id="watched"
    name="watched"
    checked={newVideo.watched}
    onChange={(e) => setNewVideo({ ...newVideo, watched: e.target.checked })}
  />
</div>
      <button type="submit">Ajouter une vidéo</button>
    </form>
  );
}

export default VideoForm;
